   178→        const mobileAccountLink = document.getElementById('mobile-account-link');
   179→        const mobilePremiumLink = document.getElementById('mobile-premium-link');
   180→        
   181→        if (accountLink) accountLink.classList.remove('hidden');
   182→        if (premiumLink) premiumLink.classList.remove('hidden');
   183→        if (mobileAccountLink) mobileAccountLink.classList.remove('hidden');
   184→        if (mobilePremiumLink) mobilePremiumLink.classList.remove('hidden');
   185→    }
