const express = require('express');
const bodyParser = require('body-parser');
const bcrypt = require('bcrypt');
const fs = require('fs');
const path = require('path');
const crypto = require('crypto');
const session = require('express-session');
const passport = require('passport');
const GoogleStrategy = require('passport-google-oauth20').Strategy;
const stripe = require('stripe')(process.env.STRIPE_SECRET_KEY);
require('dotenv').config();

const app = express();
const PORT = 3000;

// Stripe webhook endpoint needs raw body
app.post('/api/stripe/webhook', express.raw({type: 'application/json'}), handleStripeWebhook);

// Middleware
app.use(bodyParser.json());
app.use(express.static('./'));
app.use(session({
    secret: process.env.SESSION_SECRET || 'your-session-secret',
    resave: false,
    saveUninitialized: false,
    cookie: { secure: false } // Set to true in production with HTTPS
}));
app.use(passport.initialize());
app.use(passport.session()); // Serve static files

// Ensure data files exist
const usersFile = path.join(__dirname, 'users.json');
const subscriptionsFile = path.join(__dirname, 'subscriptions.json');

if (!fs.existsSync(usersFile)) {
    fs.writeFileSync(usersFile, JSON.stringify([]));
}

if (!fs.existsSync(subscriptionsFile)) {
    fs.writeFileSync(subscriptionsFile, JSON.stringify([]));
}

// Helper functions
function readUsers() {
    try {
        return JSON.parse(fs.readFileSync(usersFile, 'utf8'));
    } catch (error) {
        console.error('Error reading users file:', error);
        return [];
    }
}

function writeUsers(users) {
    try {
        fs.writeFileSync(usersFile, JSON.stringify(users, null, 2));
        return true;
    } catch (error) {
        console.error('Error writing users file:', error);
        return false;
    }
}

function isValidEmail(email) {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
}

function generateUserId() {
    return crypto.randomBytes(16).toString('hex');
}

// Passport configuration
passport.use(new GoogleStrategy({
    clientID: process.env.GOOGLE_CLIENT_ID,
    clientSecret: process.env.GOOGLE_CLIENT_SECRET,
    callbackURL: "/auth/google/callback",
    scope: ['profile', 'email']
}, async (accessToken, refreshToken, profile, done) => {
    try {
        const users = readUsers();

        // Check if user already exists
        let user = users.find(u => u.email === profile.emails[0].value);

        if (user) {
            // Update last login
            user.lastLogin = new Date().toISOString();
            writeUsers(users);
            return done(null, user);
        } else {
            // Create new user
            const newUser = {
                id: generateUserId(),
                firstName: profile.name.givenName,
                lastName: profile.name.familyName,
                email: profile.emails[0].value,
                password: null, // No password for Google users
                country: 'Unknown',
                favoriteTeams: [],
                newsletter: false,
                createdAt: new Date().toISOString(),
                verified: true, // Google accounts are pre-verified
                verificationToken: null,
                lastLogin: new Date().toISOString(),
                isActive: true,
                provider: 'google',
                googleId: profile.id
            };

            users.push(newUser);
            writeUsers(users);
            return done(null, newUser);
        }
    } catch (error) {
        return done(error, null);
    }
}));

passport.serializeUser((user, done) => {
    done(null, user.id);
});

passport.deserializeUser((id, done) => {
    const users = readUsers();
    const user = users.find(u => u.id === id);
    done(null, user);
});

// Endpoint to create account
app.post('/api/create-account', async (req, res) => {
    try {
        const { 
            firstName, 
            lastName, 
            email, 
            password, 
            country, 
            favoriteTeams, 
            newsletter,
            createdAt 
        } = req.body;

        // Validation
        if (!firstName || firstName.trim().length < 2) {
            return res.status(400).json({ message: 'First name must be at least 2 characters long' });
        }

        if (!lastName || lastName.trim().length < 2) {
            return res.status(400).json({ message: 'Last name must be at least 2 characters long' });
        }

        if (!email || !isValidEmail(email)) {
            return res.status(400).json({ message: 'Please provide a valid email address' });
        }

        if (!password || password.length < 8) {
            return res.status(400).json({ message: 'Password must be at least 8 characters long' });
        }

        if (!country) {
            return res.status(400).json({ message: 'Please select your country' });
        }

        // Check if user already exists
        const users = readUsers();
        const existingUser = users.find(user => user.email.toLowerCase() === email.toLowerCase());

        if (existingUser) {
            return res.status(409).json({ message: 'An account with this email already exists' });
        }

        // Hash password
        const saltRounds = 12;
        const hashedPassword = await bcrypt.hash(password, saltRounds);

        // Create user object
        const newUser = {
            id: generateUserId(),
            firstName: firstName.trim(),
            lastName: lastName.trim(),
            email: email.toLowerCase().trim(),
            password: hashedPassword,
            country,
            favoriteTeams: favoriteTeams || [],
            newsletter: Boolean(newsletter),
            createdAt: createdAt || new Date().toISOString(),
            verified: false,
            verificationToken: crypto.randomBytes(32).toString('hex'),
            lastLogin: null,
            isActive: true,
            subscription: {
                tier: 'free',
                status: 'inactive',
                stripeCustomerId: null,
                stripeSubscriptionId: null,
                currentPeriodEnd: null
            }
        };

        // Add user to database
        users.push(newUser);

        if (!writeUsers(users)) {
            return res.status(500).json({ message: 'Failed to save user data. Please try again.' });
        }

        // If user subscribed to newsletter, add to subscriptions
        if (newsletter) {
            try {
                const subscriptions = JSON.parse(fs.readFileSync(subscriptionsFile, 'utf8'));
