   143    function checkHeaderAuthState() {
   144        // Check for user data in localStorage
   145        const userData = localStorage.getItem('user');
   146        
   147        if (userData) {
   148            try {
   149                const user = JSON.parse(userData);
   150                showSignedInState(user);
   151                showHeaderAccountWidget(user);
   152            } catch (error) {
   153                console.error('Error parsing user data:', error);
   154                showSignedOutState();
   155                hideHeaderAccountWidget();
   156            }
   157        } else {
   158            showSignedOutState();
   159            hideHeaderAccountWidget();
   160        }
   161    }
