   163    function showSignedInState(user) {
   164        // Hide sign in/create account links
   165        const signinLink = document.getElementById('signin-link');
   166        const createAccountLink = document.getElementById('create-account-link');
   167        const mobileSigninLink = document.getElementById('mobile-signin-link');
   168        const mobileCreateAccountLink = document.getElementById('mobile-create-account-link');
   169        
   170        if (signinLink) signinLink.classList.add('hidden');
   171        if (createAccountLink) createAccountLink.classList.add('hidden');
   172        if (mobileSigninLink) mobileSigninLink.classList.add('hidden');
   173        if (mobileCreateAccountLink) mobileCreateAccountLink.classList.add('hidden');
   174        
   175        // Show account and premium links
   176        const accountLink = document.getElementById('account-link');
   177        const premiumLink = document.getElementById('premium-link');
   178        const mobileAccountLink = document.getElementById('mobile-account-link');
   179        const mobilePremiumLink = document.getElementById('mobile-premium-link');
   180        
   181        if (accountLink) accountLink.classList.remove('hidden');
   182        if (premiumLink) premiumLink.classList.remove('hidden');
   183        if (mobileAccountLink) mobileAccountLink.classList.remove('hidden');
   184        if (mobilePremiumLink) mobilePremiumLink.classList.remove('hidden');
   185    }
