   211    function checkPremiumMembershipForAds() {
   212        try {
   213            // Check for membership data
   214            const membershipData = localStorage.getItem('membership') || localStorage.getItem('eplMembership');
   215            
   216            if (membershipData) {
   217                const membership = JSON.parse(membershipData);
   218                
   219                // If user has premium membership, hide ads
   220                if (membership.plan && membership.plan !== 'free') {
   221                    hidePremiumAds();
   222                    showAdFreeIndicator();
   223                }
   224            }
   225        } catch (error) {
   226            console.error('Error checking membership for ads:', error);
   227        }
   228    }
   229
   230    function hidePremiumAds() {
   231        // Hide Google AdSense ads
   232        const adElements = document.querySelectorAll('.adsbygoogle, [class*="ad-"], [id*="ad-"], [class*="advertisement"], [class*="google-ads"]');
   233        adElements.forEach(ad => {
   234            ad.style.display = 'none';
   235            ad.style.visibility = 'hidden';
   236        });
   237
   238        // Also hide any ad containers or wrappers
   239        const adContainers = document.querySelectorAll('[class*="ad-container"], [class*="ad-wrapper"], [id*="ad-container"], [id*="ad-wrapper"]');
   240        adContainers.forEach(container => {
   241            container.style.display = 'none';
   242        });
   243
   244        // Add a class to body to indicate ad-free mode
   245        document.body.classList.add('ad-free-premium');
   246        
   247        console.log('Premium ad-free experience activated');
   248    }
