   272        // Update header state
   273        showSignedOutState();
   274        
   275        // Show notification
   276        showHeaderNotification('Signed out successfully!', 'success');
   277        
   278        // Reload page to reset state
   279        setTimeout(() => {
   280            window.location.reload();
   281        }, 1000);
   282    }
