   284    function showHeaderNotification(message, type = 'info') {
   285        const notification = document.createElement('div');
   286        notification.className = `header-notification ${type}`;
   287        notification.innerHTML = `
   288            <span>${message}</span>
   289            <button onclick="this.parentElement.remove()"></button>
   290        `;
   291        notification.style.cssText = `
   292            position: fixed;
   293            top: 20px;
   294            right: 20px;
   295            background: ${type === 'success' ? '#28a745' : type === 'error' ? '#dc3545' : '#007bff'};
   296            color: white;
   297            padding: 15px 20px;
   298            border-radius: 8px;
   299            box-shadow: 0 4px 15px rgba(0,0,0,0.2);
   300            z-index: 10000;
   301            display: flex;
   302            align-items: center;
   303            gap: 10px;
   304            font-weight: 600;
   305            animation: slideInRight 0.3s ease;
   306        `;
   307        
   308        document.body.appendChild(notification);
   309        
   310        // Auto-remove after 3 seconds
   311        setTimeout(() => {
   312            if (notification.parentElement) {
   313                notification.style.animation = 'slideOutRight 0.3s ease';
   314                setTimeout(() => notification.remove(), 300);
   315            }
   316        }, 3000);
   317    }
