     1<!DOCTYPE html>
     2<html lang="en">
     3<head>
     4    <meta charset="UTF-8">
     5    <meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=5.0, user-scalable=yes">
     6    <title>Welcome to Premium | EPL News Hub</title>
     7    <link rel="stylesheet" href="styles.css">
     8    <link rel="stylesheet" href="/mobile-master-optimize.css">
     9    
    10    <!-- Firebase SDK -->
    11    <script type="module">
    12        import { initializeApp } from 'https://www.gstatic.com/firebasejs/10.7.1/firebase-app.js';
    13        import { getAuth, onAuthStateChanged } from 'https://www.gstatic.com/firebasejs/10.7.1/firebase-auth.js';
    14        
    15        const firebaseConfig = {
    16            apiKey: "AIzaSyCyOey0l27yQP68oybpoodMVcvayhIHt2I",
    17            authDomain: "epl-news-hub-94c09.firebaseapp.com",
    18            projectId: "epl-news-hub-94c09",
    19            storageBucket: "epl-news-hub-94c09.firebasestorage.app",
    20            messagingSenderId: "674703933278",
    21            appId: "1:674703933278:web:90c6dd4aa9f1ace73099cf",
    22            measurementId: "G-ECM6BCQCS8"
    23        };
    24        
    25        const app = initializeApp(firebaseConfig);
    26        const auth = getAuth(app);
    27        
    28        window.auth = auth;
    29        window.onAuthStateChanged = onAuthStateChanged;
    30    </script>
    31    <style>
    32        .success-container {
    33            max-width: 600px;
    34            margin: 100px auto;
    35            padding: 40px;
    36            text-align: center;
    37            background: white;
    38            border-radius: 16px;
    39            box-shadow: 0 10px 40px rgba(0,0,0,0.1);
    40        }
    41        
    42        .success-icon {
    43            font-size: 4rem;
    44            margin-bottom: 20px;
    45        }
    46        
    47        .success-title {
    48            color: #37003c;
    49            font-size: 2.5rem;
    50            margin-bottom: 20px;
    51        }
    52        
    53        .success-message {
    54            color: #666;
    55            font-size: 1.2rem;
    56            line-height: 1.6;
    57            margin-bottom: 30px;
    58        }
    59        
    60        .success-button {
    61            background: linear-gradient(135deg, #37003c, #6f42c1);
    62            color: white;
    63            padding: 16px 32px;
    64            border: none;
    65            border-radius: 12px;
    66            font-size: 1.1rem;
    67            font-weight: 600;
    68            text-decoration: none;
    69            display: inline-block;
    70            transition: all 0.3s ease;
    71        }
    72        
    73        .success-button:hover {
    74            transform: translateY(-2px);
    75            box-shadow: 0 8px 20px rgba(55,0,60,0.3);
    76        }
    77    </style>
    78</head>
    79<body>
    80    <div class="success-container">
    81        <div class="success-icon"></div>
    82        <h1 class="success-title">Welcome to Premium!</h1>
    83        <p class="success-message">
    84            Thank you for subscribing to EPL News Hub Premium! Your membership is now active and you have access to all exclusive content, transfer insider info, and premium articles.
    85        </p>
    86        <p class="success-message">
    87            You should receive a confirmation email shortly with your subscription details.
    88        </p>
    89        <a href="/index.html" class="success-button">Start Exploring Premium Content</a>
    90    </div>
    91
    92    <script>
    93        document.addEventListener('DOMContentLoaded', function() {
    94            // Get plan and email from URL parameters
    95            const urlParams = new URLSearchParams(window.location.search);
    96            const planType = urlParams.get('plan');
    97            const userEmail = urlParams.get('email');
    98            
    99            // Initialize membership tracking
   100            initializeMembership(planType, userEmail);
   101            
   102            // Update page content based on plan
   103            updateContentForPlan(planType);
   104        });
   105        
   106        function initializeMembership(planType, userEmail) {
   107            // Create membership record in localStorage (in production, this would be stored in a database)
   108            const membershipData = {
   109                plan: planType || 'premium',
   110                status: 'active',
   111                startDate: new Date().toISOString(),
   112                email: userEmail,
   113                features: getPlanFeatures(planType),
   114                subscriptionSource: 'stripe'
   115            };
   116            
   117            // Store membership data
   118            localStorage.setItem('eplMembership', JSON.stringify(membershipData));
   119            
   120            // Log for debugging
   121            console.log('Membership initialized:', membershipData);
   122            
   123            // Check authentication state
   124            if (window.onAuthStateChanged && window.auth) {
   125                window.onAuthStateChanged(window.auth, (user) => {
   126                    if (user && userEmail && user.email === userEmail) {
   127                        // User is properly authenticated, membership is valid
   128                        console.log('Authenticated user matches subscription email');
   129                        
   130                        // Store user-specific membership data
   131                        const userMembershipKey = `eplMembership_${user.uid}`;
   132                        localStorage.setItem(userMembershipKey, JSON.stringify(membershipData));
   133                    }
   134                });
   135            }
   136        }
   137        
   138        function getPlanFeatures(planType) {
   139            const features = {
   140                'starter': [
   141                    '5 exclusive articles per week',
   142                    'Latest transfer rumors',
   143                    'Weekly team and match analysis',
   144                    'Access to all articles',
   145                    'FPL pro advice and suggested teams',
   146                    'Ad-free browsing'
   147                ],
   148                'starter-annual': [
   149                    '5 exclusive articles per week',
   150                    'Latest transfer rumors',
   151                    'Weekly team and match analysis',
   152                    'Access to all articles',
   153                    'FPL pro advice and suggested teams',
   154                    'Ad-free browsing'
   155                ],
   156                'pro': [
   157                    'Everything included in Starter',
   158                    'Unlimited exclusive articles',
   159                    'Premium transfer insider info',
   160                    'Early access to content',
   161                    'Fantasy Premier League exclusive team of the week'
   162                ],
   163                'pro-annual': [
   164                    'Everything included in Starter',
   165                    'Unlimited exclusive articles',
   166                    'Premium transfer insider info',
   167                    'Early access to content',
   168                    'Fantasy Premier League exclusive team of the week'
   169                ]
   170            };
   171            
   172            return features[planType] || features['starter'];
   173        }
   174        
   175        function updateContentForPlan(planType) {
   176            const planNames = {
   177                'starter': 'Starter',
   178                'starter-annual': 'Starter Annual',
   179                'pro': 'Pro',
   180                'pro-annual': 'Pro Annual'
   181            };
   182            
   183            const planName = planNames[planType] || 'Premium';
   184            
   185            // Update title and message
   186            const titleElement = document.querySelector('.success-title');
   187            const messageElement = document.querySelector('.success-message');
   188            
   189            if (titleElement) {
   190                titleElement.textContent = `Welcome to ${planName}!`;
   191            }
   192            
   193            if (messageElement) {
   194                const features = getPlanFeatures(planType);
   195                const featureText = features.length > 3 ? 
   196                    `including ${features.slice(0, 3).join(', ')} and more` : 
   197                    `including ${features.join(', ')}`;
   198                
   199                messageElement.innerHTML = `
   200                    Thank you for subscribing to EPL News Hub ${planName}! Your membership is now active and you have access to exclusive content ${featureText}.
   201                `;
   202            }
   203        }
   204        
   205        // Global function to check if user has membership (can be used by other pages)
   206        window.checkMembership = function() {
   207            const membership = localStorage.getItem('eplMembership');
   208            if (membership) {
   209                const data = JSON.parse(membership);
   210                return data.status === 'active' ? data : null;
   211            }
