  1745â†’<script>
  1746â†’    // Enhanced Search Functionality
  1747â†’    let searchTimeout;
  1748â†’    let isSearching = false;
  1749â†’    let searchCache = new Map();
  1750â†’    
  1751â†’    // Initialize search functionality
  1752â†’    document.addEventListener('DOMContentLoaded', function() {
  1753â†’        initializeSearch();
  1754â†’    });
  1755â†’    
  1756â†’    function initializeSearch() {
  1757â†’        const searchInput = document.getElementById('search-input');
  1758â†’        const searchBtn = document.querySelector('.search-btn');
  1759â†’        const searchClear = document.querySelector('.search-clear');
  1760â†’        
  1761â†’        if (searchInput) {
  1762â†’            // Add event listeners
  1763â†’            searchInput.addEventListener('input', handleSearchInput);
  1764â†’            searchInput.addEventListener('keydown', handleSearchKeydown);
  1765â†’            searchInput.addEventListener('focus', handleSearchFocus);
  1766â†’            searchInput.addEventListener('blur', handleSearchBlur);
  1767â†’        }
  1768â†’        
  1769â†’        if (searchClear) {
  1770â†’            searchClear.addEventListener('click', clearSearch);
  1771â†’        }
  1772â†’        
  1773â†’        // Close dropdown when clicking outside
  1774â†’        document.addEventListener('click', function(event) {
  1775â†’            const searchContainer = document.querySelector('.search-container');
  1776â†’            if (searchContainer && !searchContainer.contains(event.target)) {
  1777â†’                hideSearchResults();
  1778â†’            }
  1779â†’        });
  1780â†’    }
  1781â†’    
  1782â†’    function handleSearchInput(event) {
  1783â†’        const query = event.target.value;
  1784â†’        const searchClear = document.querySelector('.search-clear');
  1785â†’        
  1786â†’        // Show/hide clear button
  1787â†’        if (searchClear) {
  1788â†’            searchClear.style.display = query.length > 0 ? 'flex' : 'none';
  1789â†’        }
  1790â†’        
  1791â†’        // Debounced search
  1792â†’        clearTimeout(searchTimeout);
  1793â†’        if (query.length >= 2) {
  1794â†’            searchTimeout = setTimeout(() => {
  1795â†’                performInstantSearch(query);
  1796â†’            }, 300);
  1797â†’        } else {
  1798â†’            hideSearchResults();
  1799â†’        }
  1800â†’    }
  1801â†’    
  1802â†’    function handleSearchKeydown(event) {
  1803â†’        if (event.key === 'Enter') {
  1804â†’            event.preventDefault();
  1805â†’            const query = event.target.value.trim();
  1806â†’            if (query) {
  1807â†’                performSearch(query);
  1808â†’            }
  1809â†’        } else if (event.key === 'Escape') {
  1810â†’            hideSearchResults();
  1811â†’            event.target.blur();
  1812â†’        }
  1813â†’    }
  1814â†’    
  1815â†’    function handleSearchFocus(event) {
  1816â†’        const query = event.target.value;
  1817â†’        if (query.length >= 2) {
  1818â†’            performInstantSearch(query);
  1819â†’        }
  1820â†’    }
  1821â†’    
  1822â†’    function handleSearchBlur(event) {
  1823â†’        // Delay hiding to allow click events on results
  1824â†’        setTimeout(() => {
  1825â†’            if (!document.querySelector('.search-results-dropdown:hover')) {
  1826â†’                hideSearchResults();
  1827â†’            }
  1828â†’        }, 200);
  1829â†’    }
  1830â†’    
