   272→    function showHeaderNotification(message, type = 'info') {
   273→        const notification = document.createElement('div');
   274→        notification.className = `header-notification ${type}`;
   275→        notification.innerHTML = `
   276→            <span>${message}</span>
   277→            <button onclick="this.parentElement.remove()">×</button>
   278→        `;
   279→        notification.style.cssText = `
   280→            position: fixed;
   281→            top: 20px;
   282→            right: 20px;
   283→            background: ${type === 'success' ? '#28a745' : type === 'error' ? '#dc3545' : '#007bff'};
   284→            color: white;
   285→            padding: 15px 20px;
   286→            border-radius: 8px;
   287→            box-shadow: 0 4px 15px rgba(0,0,0,0.2);
   288→            z-index: 10000;
   289→            display: flex;
   290→            align-items: center;
   291→            gap: 10px;
   292→            font-weight: 600;
   293→            animation: slideInRight 0.3s ease;
   294→        `;
   295→        
   296→        document.body.appendChild(notification);
   297→        
   298→        // Auto-remove after 3 seconds
   299→        setTimeout(() => {
   300→            if (notification.parentElement) {
   301→                notification.style.animation = 'slideOutRight 0.3s ease';
   302→                setTimeout(() => notification.remove(), 300);
   303→            }
   304→        }, 3000);
   305→    }
