  1755→    
  1756→    function initializeSearch() {
  1757→        const searchInput = document.getElementById('search-input');
  1758→        const searchBtn = document.querySelector('.search-btn');
  1759→        const searchClear = document.querySelector('.search-clear');
  1760→        
  1761→        if (searchInput) {
  1762→            // Add event listeners
  1763→            searchInput.addEventListener('input', handleSearchInput);
  1764→            searchInput.addEventListener('keydown', handleSearchKeydown);
  1765→            searchInput.addEventListener('focus', handleSearchFocus);
  1766→            searchInput.addEventListener('blur', handleSearchBlur);
  1767→        }
  1768→        
  1769→        if (searchClear) {
  1770→            searchClear.addEventListener('click', clearSearch);
  1771→        }
  1772→        
  1773→        // Close dropdown when clicking outside
  1774→        document.addEventListener('click', function(event) {
  1775→            const searchContainer = document.querySelector('.search-container');
