  1781→    
  1782→    function handleSearchInput(event) {
  1783→        const query = event.target.value;
  1784→        const searchClear = document.querySelector('.search-clear');
  1785→        
  1786→        // Show/hide clear button
  1787→        if (searchClear) {
  1788→            searchClear.style.display = query.length > 0 ? 'flex' : 'none';
  1789→        }
  1790→        
  1791→        // Debounced search
  1792→        clearTimeout(searchTimeout);
  1793→        if (query.length >= 2) {
  1794→            searchTimeout = setTimeout(() => {
  1795→                performInstantSearch(query);
  1796→            }, 300);
  1797→        } else {
  1798→            hideSearchResults();
  1799→        }
  1800→    }
  1801→    
  1802→    function handleSearchKeydown(event) {
  1803→        if (event.key === 'Enter') {
  1804→            event.preventDefault();
  1805→            const query = event.target.value.trim();
  1806→            if (query) {
  1807→                performSearch(query);
  1808→            }
  1809→        } else if (event.key === 'Escape') {
  1810→            hideSearchResults();
  1811→            event.target.blur();
  1812→        }
  1813→    }
  1814→    
  1815→    function handleSearchFocus(event) {
  1816→        const query = event.target.value;
  1817→        if (query.length >= 2) {
  1818→            performInstantSearch(query);
  1819→        }
  1820→    }
  1821→    
  1822→    function handleSearchBlur(event) {
  1823→        // Delay hiding to allow click events on results
  1824→        setTimeout(() => {
  1825→            if (!document.querySelector('.search-results-dropdown:hover')) {
  1826→                hideSearchResults();
  1827→            }
  1828→        }, 200);
  1829→    }
  1830→    
  1831→    function clearSearch() {
  1832→        const searchInput = document.getElementById('search-input');
  1833→        const searchClear = document.querySelector('.search-clear');
  1834→        
  1835→        if (searchInput) {
  1836→            searchInput.value = '';
  1837→            searchInput.focus();
  1838→        }
  1839→        if (searchClear) {
  1840→            searchClear.style.display = 'none';
  1841→        }
  1842→        hideSearchResults();
  1843→    }
  1844→    
  1845→    function performInstantSearch(query) {
  1846→        if (isSearching) return;
  1847→        
  1848→        // Check cache first
  1849→        if (searchCache.has(query)) {
  1850→            displaySearchResults(searchCache.get(query), query);
  1851→            return;
  1852→        }
  1853→        
  1854→        isSearching = true;
  1855→        showSearchLoading();
  1856→        
  1857→        // Simulate API call delay
  1858→        setTimeout(() => {
  1859→            const results = searchArticles(query);
  1860→            searchCache.set(query, results);
  1861→            displaySearchResults(results, query);
  1862→            isSearching = false;
  1863→        }, 150);
  1864→    }
  1865→    
  1866→    function performSearch(query) {
  1867→        if (!query) {
