  2000→    function highlightQuery(text, query) {
  2001→        if (!query) return text;
  2002→        const regex = new RegExp(`(${query})`, 'gi');
  2003→        return text.replace(regex, '<strong style="background: #fff3cd; color: #856404;">$1</strong>');
