  2006→    function formatDate(dateStr) {
  2007→        const date = new Date(dateStr);
  2008→        const now = new Date();
  2009→        const diff = now - date;
  2010→        const days = Math.floor(diff / (1000 * 60 * 60 * 24));
  2011→        
  2012→        if (days === 0) return 'Today';
  2013→        if (days === 1) return 'Yesterday';
  2014→        if (days < 7) return `${days} days ago`;
  2015→        return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  2016→    }
