  2035→    // Smart dropdown positioning to prevent overflow
  2036→    function adjustDropdownPosition() {
  2037→        const dropdown = document.querySelector('.dropdown-menu');
  2038→        const dropdownParent = document.querySelector('.dropdown');
  2039→        
  2040→        if (dropdown && dropdownParent) {
  2041→            const rect = dropdown.getBoundingClientRect();
  2042→            const viewportWidth = window.innerWidth;
  2043→            const dropdownWidth = dropdown.offsetWidth;
  2044→            
  2045→            // Reset positioning
  2046→            dropdown.style.left = '';
  2047→            dropdown.style.right = '';
  2048→            dropdown.style.transform = '';
  2049→            
  2050→            // Check if dropdown would overflow on the right
  2051→            if (rect.right > viewportWidth - 20) {
  2052→                dropdown.style.left = 'auto';
  2053→                dropdown.style.right = '0';
  2054→                dropdown.style.transform = 'translateY(-10px) scale(0.95)';
  2055→                dropdown.style.transformOrigin = 'top right';
  2056→            } 
  2057→            // Check if dropdown would overflow on the left  
  2058→            else if (rect.left < 20) {
  2059→                dropdown.style.left = '0';
  2060→                dropdown.style.right = 'auto';
  2061→                dropdown.style.transform = 'translateY(-10px) scale(0.95)';
  2062→                dropdown.style.transformOrigin = 'top left';
  2063→            }
  2064→            // Default centered positioning
  2065→            else {
  2066→                dropdown.style.left = '50%';
  2067→                dropdown.style.right = 'auto';
  2068→                dropdown.style.transform = 'translateX(-50%) translateY(-10px) scale(0.95)';
  2069→                dropdown.style.transformOrigin = 'top center';
  2070→            }
  2071→        }
  2072→    }
