   130â†’
   131â†’    function checkHeaderAuthState() {
   132â†’        // Check for user data in localStorage
   133â†’        const userData = localStorage.getItem('user');
   134â†’        
   135â†’        if (userData) {
   136â†’            try {
   137â†’                const user = JSON.parse(userData);
   138â†’                showSignedInState(user);
   139â†’                showHeaderAccountWidget(user);
   140â†’            } catch (error) {
   141â†’                console.error('Error parsing user data:', error);
   142â†’                showSignedOutState();
   143â†’                hideHeaderAccountWidget();
   144â†’            }
   145â†’        } else {
   146â†’            showSignedOutState();
   147â†’            hideHeaderAccountWidget();
   148â†’        }
   149â†’    }
   150â†’
   151â†’    function showSignedInState(user) {
   152â†’        // Hide sign in/create account links
   153â†’        const signinLink = document.getElementById('signin-link');
   154â†’        const createAccountLink = document.getElementById('create-account-link');
   155â†’        const mobileSigninLink = document.getElementById('mobile-signin-link');
   156â†’        const mobileCreateAccountLink = document.getElementById('mobile-create-account-link');
   157â†’        
   158â†’        if (signinLink) signinLink.classList.add('hidden');
   159â†’        if (createAccountLink) createAccountLink.classList.add('hidden');
   160â†’        if (mobileSigninLink) mobileSigninLink.classList.add('hidden');
   161â†’        if (mobileCreateAccountLink) mobileCreateAccountLink.classList.add('hidden');
   162â†’        
   163â†’        // Show account and premium links
   164â†’        const accountLink = document.getElementById('account-link');
   165â†’        const premiumLink = document.getElementById('premium-link');
   166â†’        const mobileAccountLink = document.getElementById('mobile-account-link');
   167â†’        const mobilePremiumLink = document.getElementById('mobile-premium-link');
   168â†’        
   169â†’        if (accountLink) accountLink.classList.remove('hidden');
   170â†’        if (premiumLink) premiumLink.classList.remove('hidden');
   171â†’        if (mobileAccountLink) mobileAccountLink.classList.remove('hidden');
   172â†’        if (mobilePremiumLink) mobilePremiumLink.classList.remove('hidden');
   173â†’    }
   174â†’
   175â†’    function showSignedOutState() {
   176â†’        // Show sign in/create account links
   177â†’        const signinLink = document.getElementById('signin-link');
   178â†’        const createAccountLink = document.getElementById('create-account-link');
   179â†’        const mobileSigninLink = document.getElementById('mobile-signin-link');
   180â†’        const mobileCreateAccountLink = document.getElementById('mobile-create-account-link');
   181â†’        
   182â†’        if (signinLink) signinLink.classList.remove('hidden');
   183â†’        if (createAccountLink) createAccountLink.classList.remove('hidden');
   184â†’        if (mobileSigninLink) mobileSigninLink.classList.remove('hidden');
   185â†’        if (mobileCreateAccountLink) mobileCreateAccountLink.classList.remove('hidden');
   186â†’        
   187â†’        // Hide account and premium links
   188â†’        const accountLink = document.getElementById('account-link');
   189â†’        const premiumLink = document.getElementById('premium-link');
   190â†’        const mobileAccountLink = document.getElementById('mobile-account-link');
   191â†’        const mobilePremiumLink = document.getElementById('mobile-premium-link');
   192â†’        
   193â†’        if (accountLink) accountLink.classList.add('hidden');
   194â†’        if (premiumLink) premiumLink.classList.add('hidden');
   195â†’        if (mobileAccountLink) mobileAccountLink.classList.add('hidden');
   196â†’        if (mobilePremiumLink) mobilePremiumLink.classList.add('hidden');
   197â†’    }
   198â†’
   199â†’    function checkPremiumMembershipForAds() {
   200â†’        try {
   201â†’            // Check for membership data
   202â†’            const membershipData = localStorage.getItem('membership') || localStorage.getItem('eplMembership');
   203â†’            
   204â†’            if (membershipData) {
   205â†’                const membership = JSON.parse(membershipData);
   206â†’                
   207â†’                // If user has premium membership, hide ads
   208â†’                if (membership.plan && membership.plan !== 'free') {
   209â†’                    hidePremiumAds();
   210â†’                    showAdFreeIndicator();
   211â†’                }
   212â†’            }
   213â†’        } catch (error) {
   214â†’            console.error('Error checking membership for ads:', error);
   215â†’        }
   216â†’    }
   217â†’
   218â†’    function hidePremiumAds() {
   219â†’        // Hide Google AdSense ads
   220â†’        const adElements = document.querySelectorAll('.adsbygoogle, [class*="ad-"], [id*="ad-"], [class*="advertisement"], [class*="google-ads"]');
   221â†’        adElements.forEach(ad => {
   222â†’            ad.style.display = 'none';
   223â†’            ad.style.visibility = 'hidden';
   224â†’        });
   225â†’
   226â†’        // Also hide any ad containers or wrappers
   227â†’        const adContainers = document.querySelectorAll('[class*="ad-container"], [class*="ad-wrapper"], [id*="ad-container"], [id*="ad-wrapper"]');
   228â†’        adContainers.forEach(container => {
   229â†’            container.style.display = 'none';
   230â†’        });
   231â†’
   232â†’        // Add a class to body to indicate ad-free mode
   233â†’        document.body.classList.add('ad-free-premium');
   234â†’        
   235â†’        console.log('Premium ad-free experience activated');
   236â†’    }
