   199→    function checkPremiumMembershipForAds() {
   200→        try {
   201→            // Check for membership data
   202→            const membershipData = localStorage.getItem('membership') || localStorage.getItem('eplMembership');
   203→            
   204→            if (membershipData) {
   205→                const membership = JSON.parse(membershipData);
   206→                
   207→                // If user has premium membership, hide ads
   208→                if (membership.plan && membership.plan !== 'free') {
   209→                    hidePremiumAds();
   210→                    showAdFreeIndicator();
   211→                }
   212→            }
   213→        } catch (error) {
   214→            console.error('Error checking membership for ads:', error);
   215→        }
   216→    }
   217→
   218→    function hidePremiumAds() {
   219→        // Hide Google AdSense ads
   220→        const adElements = document.querySelectorAll('.adsbygoogle, [class*="ad-"], [id*="ad-"], [class*="advertisement"], [class*="google-ads"]');
   221→        adElements.forEach(ad => {
   222→            ad.style.display = 'none';
   223→            ad.style.visibility = 'hidden';
   224→        });
   225→
   226→        // Also hide any ad containers or wrappers
   227→        const adContainers = document.querySelectorAll('[class*="ad-container"], [class*="ad-wrapper"], [id*="ad-container"], [id*="ad-wrapper"]');
   228→        adContainers.forEach(container => {
   229→            container.style.display = 'none';
   230→        });
   231→
   232→        // Add a class to body to indicate ad-free mode
   233→        document.body.classList.add('ad-free-premium');
   234→        
   235→        console.log('Premium ad-free experience activated');
   236→    }
