   250→    function headerSignOut() {
   251→        // Clear user data
   252→        localStorage.removeItem('user');
   253→        localStorage.removeItem('membership');
   254→        localStorage.removeItem('eplMembership');
   255→        localStorage.removeItem('authToken');
   256→        
   257→        // Hide account widget
   258→        hideHeaderAccountWidget();
   259→        
