   261→        showSignedOutState();
   262→        
   263→        // Show notification
   264→        showHeaderNotification('Signed out successfully!', 'success');
   265→        
   266→        // Reload page to reset state
   267→        setTimeout(() => {
   268→            window.location.reload();
   269→        }, 1000);
   270→    }
