   286→    function showHeaderNotification(message, type = 'info') {
   287→        const notification = document.createElement('div');
   288→        notification.className = `header-notification ${type}`;
   289→        notification.innerHTML = `
   290→            <span>${message}</span>
   291→            <button onclick="this.parentElement.remove()">×</button>
   292→        `;
   293→        notification.style.cssText = `
   294→            position: fixed;
   295→            top: 20px;
   296→            right: 20px;
   297→            background: ${type === 'success' ? '#28a745' : type === 'error' ? '#dc3545' : '#007bff'};
   298→            color: white;
   299→            padding: 15px 20px;
   300→            border-radius: 8px;
   301→            box-shadow: 0 4px 15px rgba(0,0,0,0.2);
   302→            z-index: 10000;
   303→            display: flex;
   304→            align-items: center;
   305→            gap: 10px;
   306→            font-weight: 600;
   307→            animation: slideInRight 0.3s ease;
   308→        `;
   309→        
   310→        document.body.appendChild(notification);
   311→        
   312→        // Auto-remove after 3 seconds
   313→        setTimeout(() => {
   314→            if (notification.parentElement) {
   315→                notification.style.animation = 'slideOutRight 0.3s ease';
   316→                setTimeout(() => notification.remove(), 300);
   317→            }
   318→        }, 3000);
   319→    }
