  1748→        
  1749→        if (days === 0) return 'Today';
  1750→        if (days === 1) return 'Yesterday';
  1751→        if (days < 7) return `${days} days ago`;
  1752→        return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
