/* Simple EPL News Hub Styles */

/* FPL Tools Promotion Banner */
.fpl-tools-banner {
    background: linear-gradient(135deg, #38003c 0%, #00ff87 100%);
    padding: 20px;
    position: relative;
    box-shadow: 0 4px 15px rgba(0,0,0,0.2);
    animation: slideDown 0.5s ease;
    margin-bottom: 20px;
}

@keyframes slideDown {
    from {
        transform: translateY(-100%);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

.fpl-banner-content {
    max-width: 1200px;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: space-between;
    flex-wrap: wrap;
    gap: 20px;
}

.fpl-banner-left {
    flex: 1;
    min-width: 250px;
}

.fpl-banner-title {
    color: white;
    font-size: 1.8rem;
    margin-bottom: 5px;
    font-weight: 900;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
}

.fpl-banner-text {
    color: rgba(255,255,255,0.95);
    font-size: 1.1rem;
}

.fpl-banner-features {
    display: flex;
    gap: 15px;
    flex-wrap: wrap;
}

.fpl-feature {
    background: rgba(255,255,255,0.2);
    color: white;
    padding: 8px 15px;
    border-radius: 20px;
    font-weight: 600;
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255,255,255,0.3);
    text-decoration: none;
    display: inline-block;
    transition: all 0.3s ease;
}

.fpl-feature:hover {
    background: rgba(255,255,255,0.3);
    transform: translateY(-2px);
    box-shadow: 0 4px 10px rgba(0,0,0,0.2);
}

.fpl-banner-actions {
    display: flex;
    gap: 10px;
    flex-wrap: wrap;
}

.fpl-banner-btn {
    padding: 12px 25px;
    text-decoration: none;
    border-radius: 25px;
    font-weight: 700;
    transition: all 0.3s ease;
    display: inline-block;
}

.fpl-banner-btn.primary {
    background: linear-gradient(135deg, #ffd700, #ffed4e);
    color: #38003c;
    box-shadow: 0 4px 10px rgba(255,215,0,0.4);
}

.fpl-banner-btn.primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(255,215,0,0.6);

