/* Universal Responsive Design for EPL News Hub */

/* Base Responsive Setup */
* {
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
}

body {
    overflow-x: hidden;
}

/* Ensure Images are Responsive */
img {
    max-width: 100%;
    height: auto;
}

/* Prevent Horizontal Scroll */
.container,
.content-wrapper,
.header-content,
.nav-content,
.footer,
.main-content {
    max-width: 100%;
    overflow-x: hidden;
}

/* Improved Touch Targets */
.nav-links a,
.search-btn,
.menu-btn,
.newsletter-btn,
.newsletter-banner-btn,
button,
.btn {
    min-height: 44px;
    min-width: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
}

/* Better Focus States for Accessibility */
input:focus,
button:focus,

