   657→        const cookies = document.cookie.split(';');
   658→        const bannerClosed = cookies.some(cookie => cookie.trim().startsWith('fplBannerClosed='));
   659→        
   660→        if (!bannerClosed) {
   661→            // Show popup after a delay (reduced to 1.5 seconds for better UX)
   662→            setTimeout(() => {
   663→                // Remove any existing newsletter popups first
   664→                const existingPopups = document.querySelectorAll('.popup-overlay, #popupForm');
   665→                existingPopups.forEach(popup => popup.remove());
   666→                
   667→                const popupDiv = document.createElement('div');
   668→                popupDiv.innerHTML = createBigBannerPopup();
   669→                document.body.appendChild(popupDiv.firstElementChild);
   670→                
   671→                // Ensure body scroll is disabled when popup is shown
   672→                document.body.style.overflow = 'hidden';
   673→                
   674→                // Track impression
