    background: linear-gradient(145deg, #ffffff, #f8f9fa) !important;
    border: 1px solid rgba(56, 0, 60, 0.1) !important;
    border-radius: 16px !important;
    overflow: hidden !important;
    position: relative !important;
    transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94) !important;
}

.modern-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(145deg, rgba(0, 255, 135, 0.05), rgba(56, 0, 60, 0.05));
    opacity: 0;
    transition: opacity 0.3s ease;
    z-index: 1;
}

.modern-card:hover::before {
    opacity: 1;
}

.modern-card:hover {
    transform: translateY(-8px) scale(1.02) !important;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15), 0 5px 15px rgba(56, 0, 60, 0.1) !important;
    border-color: rgba(0, 255, 135, 0.3) !important;
}

/* Animated Badge */
.ad-badge-animated {
    position: absolute !important;
    top: -2px !important;
    right: -2px !important;
    background: linear-gradient(135deg, #ff3366, #ff6633, #ff3366) !important;
    background-size: 200% 200% !important;
    color: white !important;
    padding: 8px 16px !important;
    border-radius: 0 14px 0 14px !important;
    font-size: 0.7rem !important;
    font-weight: 800 !important;
    letter-spacing: 0.5px !important;
    text-transform: uppercase !important;
    animation: gradientShift 3s ease infinite, pulse 2s ease-in-out infinite !important;
    z-index: 10 !important;
    box-shadow: 0 4px 15px rgba(255, 51, 102, 0.4) !important;
}

@keyframes gradientShift {
    0%, 100% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
}

@keyframes pulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
}

