.constraints-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 25px;
}

.constraint-section h3 {
    color: #37003c;
    margin-bottom: 15px;
    font-size: 1.2rem;
    font-weight: 600;
}

.budget-input {
    display: flex;
