toolUseResult.newString:
/* Enhanced styling for better polish */
.content-section p {
    font-size: 1.1rem;
    line-height: 1.7;
    color: #374151;
    margin-bottom: 20px;
}

.impact-section {
    background: linear-gradient(135deg, #f8fafc, #e2e8f0);
    padding: 24px;
    border-radius: 8px;
    margin: 30px 0;
    border-left: 4px solid #6366f1;
}

.impact-title {
    font-size: 1.4rem;
    font-weight: 600;
    color: #1e293b;
    margin-bottom: 16px;
    display: flex;
    align-items: center;
    gap: 10px;
}

.probability-bar {
    background: #e2e8f0;
    height: 8px;
    border-radius: 4px;
    margin: 16px 0;
    overflow: hidden;
}

.probability-fill {
    background: linear-gradient(90deg, #dc2626, #ef4444);
    height: 100%;
    width: 85%;
    border-radius: 4px;
    transition: width 0.3s ease;
}

.section-divider {
    height: 2px;
    background: linear-gradient(90deg, transparent, #e2e8f0, transparent);
    margin: 40px 0;
}

.key-points {
    background: #fefefe;
    border: 1px solid #e2e8f0;
    border-radius: 8px;
    padding: 20px;
    margin: 24px 0;
}

.key-points h3 {
    color: #37003c;
    margin-bottom: 16px;
    font-size: 1.2rem;
    display: flex;
    align-items: center;
    gap: 8px;
}

.key-points ul {
    list-style: none;
    padding: 0;
}

.key-points li {
    padding: 8px 0;
    padding-left: 24px;
    position: relative;
}

.key-points li::before {
    content: '▶️';
    position: absolute;
    left: 0;
    top: 8px;
}

.transfer-implications {
    background: #37003c;
    color: white;
    padding: 24px;
    border-radius: 8px;
    margin: 30px 0;
}

.transfer-implications h3 {
    color: #ffffff;
    margin-bottom: 16px;
    font-size: 1.3rem;
}

.transfer-implications p {
    color: #e2e8f0;
}

@media (max-width: 768px) {
    .modern-article {
        padding: 16px;
    }

    .article-title {
        font-size: 1.8rem;
    }

    .article-meta {
        flex-direction: column;
        gap: 8px;
    }

    .stat-grid {
        grid-template-columns: repeat(2, 1fr);
        gap: 12px;
    }

    .hero-image {
        height: 250px;
    }

    .timeline-item {
        padding-left: 50px;
    }

    .bid-amount {
        font-size: 2rem;
    }

    .content-section p {
        font-size: 1rem;
        line-height: 1.6;
    }
}

