     4→
     5→/* ==================== FOUNDATION ==================== */
     6→
     7→/* Viewport and Base Settings */
     8→@viewport {
     9→    width: device-width;
    10→    zoom: 1.0;
    11→}
    12→
    13→/* Prevent horizontal overflow globally */
    14→html {
    15→    overflow-x: hidden !important;
    16→    width: 100% !important;
    17→    -webkit-text-size-adjust: 100%;
    18→    -ms-text-size-adjust: 100%;
    19→}
    20→
    21→body {
    22→    overflow-x: hidden !important;
    23→    width: 100% !important;
    24→    min-height: 100vh;
    25→    -webkit-font-smoothing: antialiased;
    26→    -moz-osx-font-smoothing: grayscale;
    27→    touch-action: pan-y;
    28→}
    29→
    30→/* Box sizing for all elements */
    31→*, *::before, *::after {
    32→    box-sizing: border-box !important;
    33→}
    34→
    35→/* ==================== MOBILE BREAKPOINTS ==================== */
    36→
    37→/* Small Mobile (320px - 480px) */
    38→@media (max-width: 480px) {
    39→    body {
    40→        font-size: 14px !important;
    41→    }
    42→    
    43→    .container, .main-content, .content-wrapper {
    44→        padding: 10px !important;
    45→        margin: 0 !important;
    46→    }
    47→}
