   132→    }
   133→    
   134→    /* Hide desktop navigation */
   135→    .main-nav, 
   136→    .desktop-nav,
   137→    .nav-links:not(.mobile-nav-links) {
   138→        display: none !important;
   139→    }
   140→    
   141→    /* Mobile menu button */
   142→    .menu-btn, .mobile-menu-toggle {
   143→        display: block !important;
   144→        font-size: 1.5rem !important;
   145→        padding: 10px !important;
   146→        background: none !important;
   147→        border: none !important;
   148→        cursor: pointer !important;
   149→    }
   150→    
   151→    /* ========== FORMS & INPUTS ========== */
   152→    input[type="text"],
   153→    input[type="email"],
   154→    input[type="password"],
   155→    input[type="number"],
   156→    input[type="tel"],
   157→    input[type="search"],
   158→    input[type="date"],
   159→    input[type="time"],
   160→    select,
   161→    textarea {
   162→        width: 100% !important;
   163→        padding: 12px 15px !important;
