   214→        padding: 0 !important;
   215→    }
   216→    
   217→    .card,
   218→    .tool-card,
   219→    .player-card,
   220→    .news-card,
   221→    .feature-card {
   222→        width: 100% !important;
   223→        margin-bottom: 15px !important;
   224→        padding: 15px !important;
   225→        border-radius: 12px !important;
   226→    }
   227→    
   228→    /* Two column grids on larger phones */
