   230→        .grid-2col {
   231→            grid-template-columns: repeat(2, 1fr) !important;
   232→            gap: 10px !important;
   233→        }
   234→    }
   235→    
   236→    /* ========== TABLES ========== */
   237→    table {
   238→        width: 100% !important;
   239→        display: block !important;
   240→        overflow-x: auto !important;
   241→        -webkit-overflow-scrolling: touch !important;
   242→    }
   243→    
   244→    .table-wrapper {
