     1→// Mobile Page Switcher for EPL News Hub
     2→(function() {
     3→    'use strict';
     4→    
     5→    // Enhanced mobile detection
     6→    const MobileDetector = {
     7→        // Check user agent for mobile devices
     8→        isMobileDevice() {
     9→            const userAgent = navigator.userAgent.toLowerCase();
    10→            const mobileKeywords = [
    11→                'android', 'webos', 'iphone', 'ipad', 'ipod', 
    12→                'blackberry', 'iemobile', 'opera mini', 'mobile',
    13→                'tablet', 'kindle', 'silk', 'phone'
    14→            ];
    15→            
    16→            return mobileKeywords.some(keyword => userAgent.includes(keyword));
    17→        },
    18→        
    19→        // Check screen size
    20→        isMobileScreen() {
    21→            return window.innerWidth <= 768 || window.innerHeight <= 600;
    22→        },
    23→        
    24→        // Check touch support
    25→        isTouchDevice() {
    26→            return 'ontouchstart' in window || navigator.maxTouchPoints > 0;
    27→        },
    28→        
    29→        // Combined mobile detection
    30→        isMobile() {
    31→            return this.isMobileDevice() || (this.isMobileScreen() && this.isTouchDevice());
    32→        },
    33→        
    34→        // Check if user prefers mobile experience
    35→        prefersMobile() {
    36→            return localStorage.getItem('preferMobile') === 'true';
    37→        },
    38→        
    39→        // Check if user prefers desktop experience
    40→        prefersDesktop() {
    41→            return localStorage.getItem('preferDesktop') === 'true';
    42→        }
    43→    };
    44→    
    45→    // Page switcher functionality
    46→    const PageSwitcher = {
    47→        init() {
    48→            this.checkAndSwitchPage();
    49→            this.addViewToggleButtons();
    50→            this.handleOrientationChange();
    51→        },
    52→        
    53→        checkAndSwitchPage() {
    54→            const isMobile = MobileDetector.isMobile();
    55→            const prefersMobile = MobileDetector.prefersMobile();
    56→            const prefersDesktop = MobileDetector.prefersDesktop();
    57→            
    58→            // Don't switch if user has explicitly chosen a preference
    59→            if (prefersDesktop && !prefersMobile) {
    60→                return;
    61→            }
    62→            
    63→            // Switch to mobile page if mobile device or user prefers mobile
    64→            if (isMobile || prefersMobile) {
    65→                this.loadMobilePage();
    66→            }
    67→        },
    68→        
    69→        loadMobilePage() {
    70→            // Check if we're already on mobile page
    71→            if (document.body.classList.contains('mobile-page-active')) {
    72→                return;
    73→            }
    74→            
    75→            // Add mobile page class
    76→            document.body.classList.add('mobile-page-active');
    77→            
    78→            // Replace page content with mobile version
    79→            this.replaceMobileContent();
    80→            
    81→            // Load mobile-specific styles
    82→            this.loadMobileStyles();
    83→            
    84→            // Initialize mobile-specific functionality
    85→            this.initMobileFeatures();
    86→        },
    87→        
    88→        replaceMobileContent() {
    89→            // Create mobile page structure
    90→            const mobileHTML = `
    91→                <div class="mobile-page-wrapper">
    92→                    <!-- Mobile Header -->
    93→                    <header class="mobile-header">
    94→                        <div class="mobile-header-content">
