    position: sticky;
    top: 0;
    z-index: 10;
}

/* Buttons */
button, .btn {
    padding: 12px 24px;
    font-size: 1rem;
    font-weight: 600;
    border-radius: 8px;
    border: none;
    cursor: pointer;
    transition: all 0.3s ease;
    display: inline-block;
    text-decoration: none;
    text-align: center;
}

.btn-primary {
    background: linear-gradient(135deg, #37003c, #6f42c1);
    color: white;
}

.btn-primary:hover {
    transform: translateY(-2px);
