   498â†’function calculateSimilarity(str1, str2) {
   499â†’    const maxLen = Math.max(str1.length, str2.length);
   500â†’    if (maxLen === 0) return 1.0;
   501â†’    
   502â†’    const distance = levenshteinDistance(str1, str2);
