   527â†’        if (similarity >= threshold) {
   528â†’            const best = players.reduce((a, b) => 
   529â†’                calculatePlayerScore(a, config) > calculatePlayerScore(b, config) ? a : b
   530â†’            );
   531â†’            matches.push({
