   544â†’function levenshteinDistance(str1, str2) {
   545â†’    const matrix = [];
   546â†’    
   547â†’    for (let i = 0; i <= str2.length; i++) {
   548â†’        matrix[i] = [i];
   549â†’    }
   550â†’    
   551â†’    for (let j = 0; j <= str1.length; j++) {
   552â†’        matrix[0][j] = j;
   553â†’    }
   554â†’    
   555â†’    for (let i = 1; i <= str2.length; i++) {
   556â†’        for (let j = 1; j <= str1.length; j++) {
   557â†’            if (str2.charAt(i - 1) === str1.charAt(j - 1)) {
   558â†’                matrix[i][j] = matrix[i - 1][j - 1];
   559â†’            } else {
   560â†’                matrix[i][j] = Math.min(
   561â†’                    matrix[i - 1][j - 1] + 1,
   562â†’                    matrix[i][j - 1] + 1,
   563â†’                    matrix[i - 1][j] + 1
   564â†’                );
   565â†’            }
   566â†’        }
   567â†’    }
   568â†’    
   569â†’    return matrix[str2.length][str1.length];
   570â†’}
