async function processTeamImage(event) {
    const file = event.target.files[0];
    if (!file) return;

    const statusDiv = document.getElementById('imageProcessingStatus');
    if (statusDiv) {
        statusDiv.style.display = 'block';
        statusDiv.innerHTML = '<div class="spinner" style="width: 20px; height: 20px; margin: 0 auto;"></div><p style="color: white; margin-top: 10px;">Processing screenshot...</p>';
    }

    // Create image element to display
    const img = new Image();
    const reader = new FileReader();

    reader.onload = async function(e) {
        img.src = e.target.result;

        // Wait for image to load
        await new Promise(resolve => {
            img.onload = resolve;
        });

        try {
            // Apply image preprocessing if available
            let processedImageSrc = img.src;
            if (typeof preprocessImageForOCR === 'function') {
                console.log('Applying image preprocessing for better OCR...');
                processedImageSrc = await preprocessImageForOCR(img);
            }

            // Initialize Tesseract worker with optimized settings
            const worker = await Tesseract.createWorker('eng', 1, {
                logger: m => {
                    if (m.status === 'recognizing text' && statusDiv) {
                        const progress = Math.round(m.progress * 100);
                        statusDiv.innerHTML = `<div class="spinner" style="width: 20px; height: 20px; margin: 0 auto;"></div><p style="color: white; margin-top: 10px;">Reading text... ${progress}%</p>`;
                    }
                }
            });

            try {
                // Configure OCR for better accuracy
                await worker.setParameters({
                    tessedit_char_whitelist: 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789-\' Ã±Ã¼Ã¶Ã¤Ã©Ã­Ã³ÃºÃ¡Ã Ã¨Ã¬Ã²Ã¹Ã¢ÃªÃ®Ã´Ã»Ã§Ã‘ÃœÃ–Ã„Ã‰ÃÃ“ÃšÃÃ€ÃˆÃŒÃ’Ã™Ã‚ÃŠÃŽÃ”Ã›Ã‡',
                    preserve_interword_spaces: '1',
                    tessedit_pageseg_mode: '6' // Uniform block of text
                });

                // Perform OCR on preprocessed image
                const { data } = await worker.recognize(processedImageSrc);
                console.log('OCR Result:', data);

                // Apply specific player corrections if available
                let correctedText = data.text;
                if (typeof applySpecificPlayerCorrections === 'function') {
                    correctedText = applySpecificPlayerCorrections(data.text);
                    console.log('Applied specific player corrections');
                }

                // Update status
                if (statusDiv) {
                    statusDiv.innerHTML = '<div class="spinner" style="width: 20px; height: 20px; margin: 0 auto;"></div><p style="color: white; margin-top: 10px;">Matching players...</p>';
                }

                // Process the OCR text with enhanced algorithm
                let detectedPlayers;
                if (typeof enhancedProcessOCRText === 'function') {
                    // Use enhanced processing if available
                    console.log('Using enhanced OCR processing...');
                    // Pass corrected text along with original data
                    const enhancedData = { ...data, text: correctedText };
                    detectedPlayers = enhancedProcessOCRText(enhancedData.text, enhancedData.words, enhancedData.lines);
                } else {
                    // Fallback to original processing with corrected text
                    console.log('Using standard OCR processing...');
                    detectedPlayers = processOCRText(correctedText, data.words, data.lines);
                }

                // Extract potential names for display
                const detectedNames = extractPotentialPlayerNames(correctedText, data.words, data.lines);

                // Show the smart player picker dialog
                showSmartPlayerPicker(detectedNames, detectedPlayers);

            } finally {
                await worker.terminate();
            }
        } catch (error) {
            console.error('OCR Error:', error);
            alert('Failed to process image. Please try again or enter players manually.');
        } finally {
            if (statusDiv) {
                statusDiv.style.display = 'none';
            }
            // Reset file input
            event.target.value = '';
        }
    };

    reader.readAsDataURL(file);
}

