async function processTeamImage(event) {
    const file = event.target.files[0];
    if (!file) return;

    const statusDiv = document.getElementById('imageProcessingStatus');
    if (statusDiv) {
        statusDiv.style.display = 'block';
        statusDiv.innerHTML = '<div class="spinner" style="width: 20px; height: 20px; margin: 0 auto;"></div><p style="color: white; margin-top: 10px;">ðŸ” Analyzing screenshot with multiple methods...</p>';
    }

    try {
        // Use advanced multi-pass OCR processor
        if (typeof processScreenshotAdvanced === 'function') {
            console.log('Using advanced multi-pass OCR...');

            const detectedPlayers = await processScreenshotAdvanced(file);

            if (statusDiv) {
                statusDiv.style.display = 'none';
            }

            if (detectedPlayers.length > 0) {
                // Show review modal for detected players
                showAdvancedPlayerReview(detectedPlayers);
            } else {
                // No players found - offer manual entry
                alert('Could not detect any players in the screenshot. Please use the manual team builder instead.');
                createManualTeamBuilder();
            }

        } else {
            // Fallback to simple OCR
            console.log('Falling back to simple OCR...');

            const img = new Image();
            const reader = new FileReader();

            reader.onload = async function(e) {
                img.src = e.target.result;

                await new Promise(resolve => {
                    img.onload = resolve;
                });

                const worker = await Tesseract.createWorker('eng');

                try {
                    const { data } = await worker.recognize(img.src);

                    let detectedPlayers;
                    if (typeof simpleProcessOCR === 'function') {
                        detectedPlayers = simpleProcessOCR(data.text, data.words, data.lines);
                    } else {
                        detectedPlayers = processOCRText(data.text, data.words, data.lines);
                    }

                    if (detectedPlayers.length > 0) {
                        const detectedNames = extractPotentialPlayerNames(data.text, data.words, data.lines);
                        showSmartPlayerPicker(detectedNames, detectedPlayers);
                    } else {
                        alert('Could not detect any players. Please use the manual team builder.');
                        createManualTeamBuilder();
                    }

                } finally {
                    await worker.terminate();
                }
            };

            reader.readAsDataURL(file);
        }

    } catch (error) {
        console.error('Screenshot processing error:', error);
        alert('Failed to process screenshot. Please use the manual team builder instead.');
        createManualTeamBuilder();
    } finally {
        if (statusDiv) {
            setTimeout(() => {
                statusDiv.style.display = 'none';
            }, 500);
        }
        // Reset file input
        event.target.value = '';
    }
}

