    if (statusDiv) {
        statusDiv.style.display = 'block';
        statusDiv.innerHTML = '<div class="spinner" style="width: 20px; height: 20px; margin: 0 auto;"></div><p style="color: white; margin-top: 10px;">ðŸ” Analyzing screenshot with multiple methods...</p>';
    }

    try {
        // Use advanced multi-pass OCR processor
        if (typeof processScreenshotAdvanced === 'function') {
            console.log('Using advanced multi-pass OCR...');

            const detectedPlayers = await processScreenshotAdvanced(file);

            if (statusDiv) {
