        /([A-Z]{2,4})/g,                        // Initials (TAA, VVD, etc)
        /([A-Z][a-z]+)/g                        // Single words
    ];

    const potentialNames = new Set();

    // Extract using patterns
    patterns.forEach(pattern => {
        const matches = text.matchAll(pattern);
