}

// Helper functions for the review modal
window.removeDetectedPlayer = function(playerId) {
    if (window.reviewedPlayers) {
        window.reviewedPlayers = window.reviewedPlayers.filter(p => p.id !== playerId);
        if (window.reviewModal) {
