   433→}
   434→
   435→/* News Grid */
   436→.news-grid {
   437→    margin-top: 40px;
   438→}
   439→
   440→.section-title {
   441→    font-family: 'Helvetica Neue', sans-serif;
   442→    font-size: 1.8rem;
   443→    font-weight: 700;
   444→    color: #121212;
   445→    margin-bottom: 30px;
   446→    padding-bottom: 15px;
   447→    border-bottom: 3px solid #0066cc;
