}

.news-article-card h2 {
    margin: 1rem 0;
    font-size: 1.3rem;
}

.news-article-card h2 a {
    color: #121212;
    text-decoration: none;
}

.news-article-card h2 a:hover {
    color: #0066cc;
}

.article-excerpt {
    color: #666;
    font-size: 0.95rem;
    margin-bottom: 1rem;
}

.article-meta {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.85rem;
    color: #888;
    padding-top: 1rem;
    border-top: 1px solid #f0f0f0;
}

/* Polls Styles */
.polls-section {
    max-width: 800px;
    margin: 0 auto;
    padding: 0 20px;
}

.polls-container {
    display: flex;
    flex-direction: column;
    gap: 2rem;
}

.poll-card {
    border: 1px solid #e2e2e2;
    border-radius: 8px;
    padding: 2rem;
    background: #ffffff;
    box-shadow: 0 2px 8px rgba(0,0,0,0.05);
}

.poll-question {
    font-size: 1.3rem;
    margin-bottom: 1.5rem;
    color: #121212;
}

.poll-options {
    margin-bottom: 1.5rem;
}

.poll-option {
    display: flex;
    align-items: center;
    margin-bottom: 1rem;
    position: relative;
}

.poll-option input[type="radio"],
.poll-option input[type="checkbox"] {
    margin-right: 0.8rem;
}

.poll-option label {
    font-weight: 500;
    flex: 1;
    cursor: pointer;
}

.poll-bar {
    width: 150px;
    height: 20px;
    background-color: #f0f0f0;
    border-radius: 10px;
    margin: 0 1rem;
    position: relative;
    overflow: hidden;
}

.poll-fill {
    height: 100%;
    background-color: #0066cc;
    border-radius: 10px;
    transition: width 0.3s ease;
}

.poll-percentage {
    font-weight: 600;
    color: #0066cc;
