}

.vote-btn {
    background-color: #0066cc;
    color: white;
    border: none;
    padding: 0.8rem 1.5rem;
    border-radius: 6px;
    font-weight: 600;
    cursor: pointer;
    transition: background-color 0.2s ease;
}

.vote-btn:hover {
    background-color: #0052a3;
}

.poll-votes {
    margin-top: 0.5rem;
    color: #666;
    font-size: 0.9rem;
}

.poll-results {
    max-width: 800px;
    margin: 3rem auto 0;
    padding: 0 20px;
}

.poll-results .section-title {
    font-size: 1.8rem;
    margin-bottom: 1.5rem;
