  1207â†’            calculatePlayerScore(player, strategy, preferences) {
  1208â†’                let score = 0;
  1209â†’                
  1210â†’                // Base metrics
  1211â†’                const pointsScore = player.total_points / 10;
  1212â†’                const formScore = parseFloat(player.form) * 2;
  1213â†’                const valueScore = (player.total_points / player.now_cost) * 100;
  1214â†’                const xGIScore = (parseFloat(player.expected_goals) + parseFloat(player.expected_assists)) * 10;
  1215â†’                const minutesRatio = player.minutes / (player.starts || 1) / 90; // Average minutes per start
  1216â†’                
  1217â†’                // Strategy adjustments
  1218â†’                switch (strategy) {
  1219â†’                    case 'best':
  1220â†’                        // Focus on highest performing players regardless of cost
  1221â†’                        score = pointsScore * 0.35 + formScore * 0.35 + xGIScore * 0.2 + minutesRatio * 0.1;
  1222â†’                        // Bonus for consistent high scorers
  1223â†’                        if (player.total_points > 100) score *= 1.3;
  1224â†’                        if (player.total_points > 150) score *= 1.5;
  1225â†’                        break;
  1226â†’                    
  1227â†’                    case 'budget':
  1228â†’                        // Maximize value under budget constraints
  1229â†’                        score = valueScore * 0.45 + formScore * 0.25 + pointsScore * 0.2 + minutesRatio * 0.1;
  1230â†’                        // Boost cheaper players with good returns
  1231â†’                        if (player.now_cost < 60 && player.total_points > 50) score *= 1.4;
  1232â†’                        if (player.now_cost < 50 && player.total_points > 40) score *= 1.6;
  1233â†’                        break;
  1234â†’                    
  1235â†’                    case 'wildcard':
  1236â†’                        // Focus on form and upcoming fixtures
  1237â†’                        score = formScore * 0.45 + xGIScore * 0.25 + pointsScore * 0.2 + minutesRatio * 0.1;
  1238â†’                        // Boost in-form players
  1239â†’                        if (parseFloat(player.form) > 6) score *= 1.3;
  1240â†’                        if (parseFloat(player.form) > 8) score *= 1.5;
  1241â†’                        // Consider recent transfers
  1242â†’                        if (player.transfers_in_event > player.transfers_out_event * 2) score *= 1.2;
  1243â†’                        break;
  1244â†’                    
  1245â†’                    default: // balanced
  1246â†’                        score = pointsScore * 0.35 + formScore * 0.25 + valueScore * 0.25 + xGIScore * 0.15;
  1247â†’                }
  1248â†’
  1249â†’                // Preference adjustments
  1250â†’                if (preferences.includeCaptains && player.total_points > 80) {
  1251â†’                    score *= 1.2;
  1252â†’                }
  1253â†’                if (preferences.avoidRotation && player.minutes < 1500) {
  1254â†’                    score *= 0.8;
  1255â†’                }
  1256â†’                if (preferences.differentialPicks && player.selected_by_percent < 10) {
  1257â†’                    score *= 1.1;
  1258â†’                }
  1259â†’                if (preferences.considerFixtures) {
  1260â†’                    // Boost players from teams with easier fixtures
  1261â†’                    const easyFixtureTeams = [14, 17, 15, 1, 3]; // Example team IDs with easier fixtures
  1262â†’                    if (easyFixtureTeams.includes(player.team)) {
  1263â†’                        score *= 1.15;
  1264â†’                    }
  1265â†’                }
  1266â†’
  1267â†’                return score;
  1268â†’            }
