  1270â†’            knapsackOptimization(players, budget, count) {
  1271â†’                // Enhanced knapsack optimization for FPL
  1272â†’                const selected = [];
  1273â†’                let remainingBudget = budget;
  1274â†’                let remainingCount = count;
  1275â†’                
  1276â†’                // Group players by price ranges for better distribution
  1277â†’                const priceRanges = {
  1278â†’                    premium: players.filter(p => p.now_cost >= 100),
  1279â†’                    mid: players.filter(p => p.now_cost >= 65 && p.now_cost < 100),
  1280â†’                    budget: players.filter(p => p.now_cost < 65)
  1281â†’                };
  1282â†’                
  1283â†’                // Determine optimal distribution based on strategy
  1284â†’                const distribution = this.getOptimalDistribution(count, this.selectedStrategy);
  1285â†’                
  1286â†’                // Select from each price range
  1287â†’                for (const [range, targetCount] of Object.entries(distribution)) {
  1288â†’                    const rangePlayers = priceRanges[range];
  1289â†’                    const toSelect = Math.min(targetCount, rangePlayers.length);
  1290â†’                    
  1291â†’                    for (let i = 0; i < toSelect && remainingCount > 0; i++) {
  1292â†’                        const player = rangePlayers[i];
  1293â†’                        if (player && player.now_cost <= remainingBudget) {
  1294â†’                            selected.push(player);
  1295â†’                            remainingBudget -= player.now_cost;
  1296â†’                            remainingCount--;
  1297â†’                        }
  1298â†’                    }
  1299â†’                }
  1300â†’                
  1301â†’                // Fill remaining slots with best available within budget
  1302â†’                if (remainingCount > 0) {
  1303â†’                    const available = players
  1304â†’                        .filter(p => !selected.includes(p) && p.now_cost <= remainingBudget / remainingCount * 1.5)
  1305â†’                        .slice(0, remainingCount * 2); // Get more candidates
  1306â†’                    
  1307â†’                    // Dynamic programming approach for remaining slots
  1308â†’                    const bestCombination = this.findBestCombination(
  1309â†’                        available,
  1310â†’                        remainingBudget,
  1311â†’                        remainingCount
  1312â†’                    );
  1313â†’                    
  1314â†’                    selected.push(...bestCombination);
  1315â†’                }
  1316â†’                
  1317â†’                // Ensure we have exactly 'count' players
  1318â†’                return selected.slice(0, count);
  1319â†’            }
  1320â†’            
  1321â†’            getOptimalDistribution(count, strategy) {
  1322â†’                const distributions = {
  1323â†’                    best: { premium: Math.ceil(count * 0.4), mid: Math.ceil(count * 0.4), budget: Math.floor(count * 0.2) },
  1324â†’                    budget: { premium: Math.floor(count * 0.1), mid: Math.ceil(count * 0.3), budget: Math.ceil(count * 0.6) },
  1325â†’                    wildcard: { premium: Math.ceil(count * 0.3), mid: Math.ceil(count * 0.4), budget: Math.floor(count * 0.3) },
  1326â†’                    balanced: { premium: Math.ceil(count * 0.25), mid: Math.ceil(count * 0.45), budget: Math.floor(count * 0.3) }
  1327â†’                };
  1328â†’                
  1329â†’                return distributions[strategy] || distributions.balanced;
  1330â†’            }
  1331â†’            
  1332â†’            findBestCombination(players, budget, count) {
  1333â†’                // Simple greedy approach for performance
  1334â†’                const sorted = players.sort((a, b) => b.score - a.score);
  1335â†’                const result = [];
  1336â†’                let remaining = budget;
  1337â†’                
  1338â†’                for (const player of sorted) {
  1339â†’                    if (result.length >= count) break;
  1340â†’                    if (player.now_cost <= remaining) {
  1341â†’                        result.push(player);
  1342â†’                        remaining -= player.now_cost;
  1343â†’                    }
  1344â†’                }
  1345â†’                
  1346â†’                // If we don't have enough players, add cheapest
  1347â†’                if (result.length < count) {
  1348â†’                    const cheapest = players
  1349â†’                        .filter(p => !result.includes(p))
  1350â†’                        .sort((a, b) => a.now_cost - b.now_cost)
  1351â†’                        .slice(0, count - result.length);
  1352â†’                    result.push(...cheapest);
  1353â†’                }
  1354â†’                
  1355â†’                return result;
  1356â†’            }
