    }

    loadOptimizationHistory() {
        const saved = localStorage.getItem('fpl_optimization_history');
        return saved ? JSON.parse(saved) : [];
    }

    generateId() {
        return 'squad_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
    }

    // Clear all data
    clearAllData() {
